// src/components/Navbar.jsx
import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ toggleSidebar, darkMode, toggleDarkMode }) => {
  return (
    <nav className="bg-white dark:bg-gray-800 shadow-md flex items-center justify-between p-4">
      <div className="flex items-center">
        {/* Hamburger for Sidebar */}
        <button onClick={toggleSidebar} className="mr-4 focus:outline-none">
          <svg className="w-6 h-6 text-gray-800 dark:text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 8h16M4 16h16" />
          </svg>
        </button>
        {/* Logo linking to Home */}
        <Link to="/" className="text-xl font-bold text-indigo-600 dark:text-indigo-400">
          BuzzZ.com
        </Link>
      </div>
      {/* Search Bar */}
      <div className="flex-1 mx-4">
        <input 
          type="text" 
          placeholder="Search..." 
          className="w-full p-2 rounded border border-gray-300 focus:outline-none focus:ring-2 focus:ring-indigo-600"
        />
      </div>
      <div className="flex items-center space-x-4">
        {/* Dark Mode Toggle */}
        <button onClick={toggleDarkMode} className="focus:outline-none">
          {darkMode ? (
            <svg className="w-6 h-6 text-yellow-500" fill="currentColor" viewBox="0 0 20 20">
              <path d="M10 2a1 1 0 011 1v2a1 1 0 11-2 0V3a1 1 0 011-1zM4.222 4.222a1 1 0 011.415 0l1.414 1.414a1 1 0 11-1.414 1.414L4.222 5.636a1 1 0 010-1.414zM2 10a1 1 0 011-1h2a1 1 0 110 2H3a1 1 0 01-1-1zm2.222 5.778a1 1 0 000-1.414l1.414-1.414a1 1 0 011.414 1.414l-1.414 1.414a1 1 0 01-1.414 0zM10 16a1 1 0 011 1v2a1 1 0 11-2 0v-2a1 1 0 011-1zm5.778-1.222a1 1 0 00-1.414 0l-1.414 1.414a1 1 0 101.414 1.414l1.414-1.414a1 1 0 000-1.414zM16 10a1 1 0 00-1-1h-2a1 1 0 100 2h2a1 1 0 001-1zm-2.222-5.778a1 1 0 00-1.414 1.414l1.414 1.414a1 1 0 001.414-1.414L13.778 4.222z" />
            </svg>
          ) : (
            <svg className="w-6 h-6 text-gray-800 dark:text-gray-200" fill="currentColor" viewBox="0 0 20 20">
              <path d="M10 2a1 1 0 011 1v2a1 1 0 11-2 0V3a1 1 0 011-1zm5.364 1.636a1 1 0 10-1.414 1.414l1.414 1.414a1 1 0 101.414-1.414L15.364 3.636zM18 10a1 1 0 01-1 1h-2a1 1 0 110-2h2a1 1 0 011 1zm-1.636 5.364a1 1 0 10-1.414-1.414l-1.414 1.414a1 1 0 101.414 1.414l1.414-1.414zM10 18a1 1 0 01-1 1v-2a1 1 0 112 0v2a1 1 0 01-1 1zm-5.364-1.636a1 1 0 00-1.414 1.414l1.414 1.414a1 1 0 001.414-1.414L4.636 16.364zM4 10a1 1 0 011-1H3a1 1 0 100 2h2a1 1 0 01-1-1zm1.636-5.364a1 1 0 011.414-1.414l1.414 1.414a1 1 0 11-1.414 1.414L4.636 4.636z" />
            </svg>
          )}
        </button>
        {/* Profile Icon */}
        <Link to="/profile">
          <img src="/default-profile.png" alt="Profile" className="w-8 h-8 rounded-full" />
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
