// src/pages/HomePage.jsx
import React from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-600 to-purple-800 flex flex-col">
      {/* Header with Favicon Logo */}
      <header className="p-4 flex items-center">
        <img src="/favicon.ico" alt="BuzzZ.com logo" className="h-10 mr-3" />
        <h1 className="text-white text-3xl font-bold">BuzzZ.com</h1>
      </header>

      {/* Main Animated Section */}
      <main className="flex-grow flex flex-col items-center justify-center text-center px-4">
        <h2 className="text-white text-6xl font-extrabold mb-4 animate-bounce">
          Welcome to BuzzZ.com
        </h2>
        <p className="text-white text-xl mb-12 max-w-xl animate-fadeIn">
          Join the buzz and experience a revolutionary social media platform where creativity meets community.
        </p>
        <div className="flex space-x-6">
          <Link
            to="/login"
            className="px-8 py-4 bg-white text-indigo-600 font-semibold rounded-full shadow-xl transform transition duration-300 hover:scale-105 hover:bg-gray-200"
          >
            Login
          </Link>
          <Link
            to="/register"
            className="px-8 py-4 bg-white text-indigo-600 font-semibold rounded-full shadow-xl transform transition duration-300 hover:scale-105 hover:bg-gray-200"
          >
            Register
          </Link>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-gray-900 text-gray-400 text-center py-4">
        &copy; {new Date().getFullYear()} BuzzZ.com. All rights reserved.
      </footer>
    </div>
  );
};

export default HomePage;
