// src/components/Navbar.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import SearchBar from './SearchBar';

const Navbar = ({ toggleSidebar, darkMode, toggleDarkMode }) => {
  return (
    <nav className="bg-white dark:bg-gray-800 shadow-md flex items-center justify-between p-4 transition-colors duration-300">
      <div className="flex items-center">
        {/* Hamburger for Sidebar */}
        <button onClick={toggleSidebar} className="mr-4 focus:outline-none">
          <svg className="w-8 h-8 text-gray-800 dark:text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 8h16M4 16h16" />
          </svg>
        </button>
        {/* Logo linking to Home */}
        <Link to="/" className="text-2xl font-bold text-indigo-600 dark:text-indigo-400 transition-colors duration-300">
          BuzzZ.com
        </Link>
      </div>
      {/* Search Bar with Recommendations */}
      <div className="flex-1 mx-4">
        <SearchBar />
      </div>
      <div className="flex items-center space-x-4">
        {/* Dark Mode Toggle */}
        <button onClick={toggleDarkMode} className="focus:outline-none transition-transform duration-300 hover:scale-110">
          {darkMode ? (
            // Sun icon for light mode toggle
            <svg className="w-8 h-8 text-yellow-500" fill="currentColor" viewBox="0 0 20 20">
              <path d="M10 2a1 1 0 011 1v2a1 1 0 11-2 0V3a1 1 0 011-1zM4.222 4.222a1 1 0 011.415 0l1.414 1.414a1 1 0 11-1.414 1.414L4.222 5.636a1 1 0 010-1.414zM2 10a1 1 0 011-1h2a1 1 0 110 2H3a1 1 0 01-1-1zm2.222 5.778a1 1 0 000-1.414l1.414-1.414a1 1 0 011.414 1.414l-1.414 1.414a1 1 0 01-1.414 0zM10 16a1 1 0 011 1v2a1 1 0 11-2 0v-2a1 1 0 011-1zm5.778-1.222a1 1 0 00-1.414 0l-1.414 1.414a1 1 0 101.414 1.414l1.414-1.414a1 1 0 000-1.414zM16 10a1 1 0 00-1-1h-2a1 1 0 100 2h2a1 1 0 001-1zm-2.222-5.778a1 1 0 00-1.414 1.414l1.414 1.414a1 1 0 001.414-1.414L13.778 4.222z" />
            </svg>
          ) : (
            // Moon icon for dark mode toggle
            <svg className="w-8 h-8 text-gray-800 dark:text-gray-200" fill="currentColor" viewBox="0 0 20 20">
              <path d="M17.293 13.293A8 8 0 116.707 2.707a8.001 8.001 0 0010.586 10.586z" />
            </svg>
          )}
        </button>
        {/* Profile Icon */}
        <Link to="/profile">
          <img src="/default-profile.png" alt="Profile" className="w-10 h-10 rounded-full border border-gray-300 dark:border-gray-600" />
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
